import re


INITIAL_MOLECULE = ""


def generate_all_viable_molecules_after_single_replacement(initial_molecule, substitutions):
    result = set()

    for substitution in substitutions:
        characters_to_replace = substitution[0]
        characters_to_insert = substitution[1]

        for match in re.finditer(characters_to_replace, initial_molecule):
            generated_molecule = initial_molecule[:match.start()] + characters_to_insert + initial_molecule[match.start() + len(characters_to_replace):]
            result.add(generated_molecule)

    return result
